import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = ({ onLogout, onBack }) => {
  const [profile, setProfile] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    avatar: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile(response.data);
      setFormData({
        name: response.data.name,
        email: response.data.email,
        avatar: response.data.avatar || ''
      });
    } catch (error) {
      setMessage('‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin profile');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile(response.data.user);
      setEditMode(false);
      setMessage('‚úÖ C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i'));
    } finally {
      setLoading(false);
    }
  };

  if (loading && !profile) {
    return (
      <div className="container">
        <div className="loading">‚è≥ ƒêang t·∫£i th√¥ng tin...</div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="container">
        <div className="error">‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin profile</div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="profile-header">
        <button onClick={onBack} className="btn btn-secondary">
          ‚Üê Quay l·∫°i
        </button>
        <h1>üë§ Th√¥ng tin c√° nh√¢n</h1>
        <button onClick={onLogout} className="btn btn-danger">
          üöÄ ƒêƒÉng xu·∫•t
        </button>
      </div>
      
      <div className="profile-container">
        {!editMode ? (
          <div className="profile-view">
            <div className="avatar-section">
              <div className="avatar">
                {profile.avatar ? (
                  <img src={profile.avatar} alt="Avatar" />
                ) : (
                  <div className="avatar-placeholder">üë§</div>
                )}
              </div>
            </div>
            
            <div className="profile-info">
              <div className="info-item">
                <span className="label">üìù T√™n:</span>
                <span className="value">{profile.name}</span>
              </div>
              <div className="info-item">
                <span className="label">üìß Email:</span>
                <span className="value">{profile.email}</span>
              </div>
              <div className="info-item">
                <span className="label">üîë Vai tr√≤:</span>
                <span className="value">{profile.role === 'admin' ? 'üîë Qu·∫£n tr·ªã vi√™n' : 'üë§ Ng∆∞·ªùi d√πng'}</span>
              </div>
              <div className="info-item">
                <span className="label">üìÖ Ng√†y t·∫°o:</span>
                <span className="value">{new Date(profile.createdAt).toLocaleDateString('vi-VN')}</span>
              </div>
            </div>
            
            <button onClick={() => setEditMode(true)} className="btn btn-primary">
              ‚úèÔ∏è Ch·ªânh s·ª≠a th√¥ng tin
            </button>
          </div>
        ) : (
          <form onSubmit={handleUpdate} className="profile-form">
            <h3>‚úèÔ∏è C·∫≠p nh·∫≠t th√¥ng tin</h3>
            
            <div className="form-group">
              <label>üìù T√™n:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Nh·∫≠p t√™n c·ªßa b·∫°n"
              />
            </div>
            
            <div className="form-group">
              <label>üìß Email:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Nh·∫≠p email c·ªßa b·∫°n"
              />
            </div>
            
            <div className="form-group">
              <label>üñºÔ∏è Avatar URL (t√πy ch·ªçn):</label>
              <input
                type="url"
                name="avatar"
                value={formData.avatar}
                onChange={handleChange}
                className="form-input"
                placeholder="https://example.com/avatar.jpg"
              />
            </div>
            
            <div className="form-actions">
              <button type="submit" disabled={loading} className="btn btn-success">
                {loading ? '‚è≥ ƒêang l∆∞u...' : 'üíæ L∆∞u thay ƒë·ªïi'}
              </button>
              <button type="button" onClick={() => setEditMode(false)} className="btn btn-secondary">
                ‚ùå H·ªßy
              </button>
            </div>
          </form>
        )}
        
        {message && (
          <div className={`message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
