import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const DashboardRedux = () => {
  const { user, token } = useSelector((state) => state.auth);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalUsers: 0,
    adminCount: 0,
    userCount: 0,
    moderatorCount: 0
  });
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch user stats
      const statsResponse = await axios.get('http://localhost:3000/api/users/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(statsResponse.data);

      // Fetch recent activity if admin
      if (user?.role === 'admin') {
        const activityResponse = await axios.get('http://localhost:3000/api/activity/logs?limit=5', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setRecentActivity(activityResponse.data.logs || []);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">ƒêang t·∫£i dashboard...</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard">
        <h2>üìä Dashboard</h2>
        <p>Ch√†o m·ª´ng, <strong>{user?.name}</strong>!</p>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card" onClick={() => navigate('/users')}>
            <div className="stat-icon">üë•</div>
            <div className="stat-info">
              <h3>T·ªïng Users</h3>
              <div className="stat-number">{stats.totalUsers}</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üëë</div>
            <div className="stat-info">
              <h3>Admins</h3>
              <div className="stat-number">{stats.adminCount}</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üõ°Ô∏è</div>
            <div className="stat-info">
              <h3>Moderators</h3>
              <div className="stat-number">{stats.moderatorCount}</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üë§</div>
            <div className="stat-info">
              <h3>Users</h3>
              <div className="stat-number">{stats.userCount}</div>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3>‚ö° Quick Actions</h3>
          <div className="action-grid">
            <button onClick={() => navigate('/profile')} className="action-btn">
              <span>üë§</span>
              <div>
                <h4>View Profile</h4>
                <p>Xem v√† ch·ªânh s·ª≠a profile</p>
              </div>
            </button>

            <button onClick={() => navigate('/users')} className="action-btn">
              <span>üë•</span>
              <div>
                <h4>Manage Users</h4>
                <p>Qu·∫£n l√Ω danh s√°ch users</p>
              </div>
            </button>

            {user?.role === 'admin' && (
              <>
                <button onClick={() => navigate('/admin')} className="action-btn">
                  <span>‚öôÔ∏è</span>
                  <div>
                    <h4>Admin Panel</h4>
                    <p>Qu·∫£n tr·ªã h·ªá th·ªëng</p>
                  </div>
                </button>

                <button onClick={() => navigate('/logs')} className="action-btn">
                  <span>üìã</span>
                  <div>
                    <h4>Activity Logs</h4>
                    <p>Xem logs ho·∫°t ƒë·ªông</p>
                  </div>
                </button>
              </>
            )}
          </div>
        </div>

        {/* Recent Activity */}
        {user?.role === 'admin' && recentActivity.length > 0 && (
          <div className="recent-activity">
            <h3>üìà Recent Activity</h3>
            <div className="activity-list">
              {recentActivity.map((activity, index) => (
                <div key={index} className="activity-item">
                  <div className="activity-icon">
                    {activity.action === 'LOGIN_SUCCESS' ? 'üîì' : 
                     activity.action === 'LOGOUT' ? 'üîí' : 
                     activity.action === 'SIGNUP' ? 'üë§' : 'üìù'}
                  </div>
                  <div className="activity-content">
                    <div className="activity-title">{activity.action}</div>
                    <div className="activity-details">{activity.details}</div>
                    <div className="activity-time">
                      {new Date(activity.timestamp).toLocaleString()}
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <button onClick={() => navigate('/logs')} className="view-all-btn">
              Xem t·∫•t c·∫£ logs ‚Üí
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardRedux;
