import React, { useState } from 'react';
import api from '../services/api';

const RateLimitDemo = () => {
  const [results, setResults] = useState([]);
  const [testing, setTesting] = useState(false);
  const [testType, setTestType] = useState('login');

  const addResult = (message, success = true, statusCode = 200) => {
    setResults(prev => [...prev, {
      id: Date.now(),
      message,
      success,
      statusCode,
      timestamp: new Date().toLocaleTimeString()
    }]);
  };

  const clearResults = () => {
    setResults([]);
  };

  const testLoginRateLimit = async () => {
    setTesting(true);
    setResults([]);

    addResult('üöÄ B·∫Øt ƒë·∫ßu test Login Rate Limit (5 attempts/15 ph√∫t)...');

    for (let i = 1; i <= 7; i++) {
      try {
        addResult(`üì° Attempt ${i}: Trying to login with wrong password...`);

        const response = await api.post('/api/auth/login', {
          email: 'admin@example.com',
          password: 'wrongpassword'
        });

        addResult(`‚úÖ Attempt ${i}: Unexpected success`, true, response.status);

      } catch (error) {
        const status = error.response?.status;
        const message = error.response?.data?.message || error.message;

        if (status === 429) {
          addResult(`üö´ Attempt ${i}: RATE LIMITED! ${message}`, false, status);
          addResult('üéâ Rate limiting working correctly!', true);
          break;
        } else if (status === 400) {
          addResult(`‚ùå Attempt ${i}: Login failed (expected)`, true, status);
        } else {
          addResult(`‚ö†Ô∏è Attempt ${i}: ${message}`, false, status);
        }
      }

      // Small delay between attempts
      await new Promise(resolve => setTimeout(resolve, 500));
    }

    setTesting(false);
  };

  const testUploadRateLimit = async () => {
    setTesting(true);
    setResults([]);

    addResult('üöÄ B·∫Øt ƒë·∫ßu test Upload Rate Limit (3 attempts/ph√∫t)...');

    // Create a dummy file
    const dummyFile = new File(['dummy content'], 'test.jpg', { type: 'image/jpeg' });

    for (let i = 1; i <= 5; i++) {
      try {
        addResult(`üì§ Attempt ${i}: Trying to upload avatar...`);

        const formData = new FormData();
        formData.append('avatar', dummyFile);

        const response = await api.post('/api/avatar/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        addResult(`‚úÖ Attempt ${i}: Upload success`, true, response.status);

      } catch (error) {
        const status = error.response?.status;
        const message = error.response?.data?.message || error.message;

        if (status === 429) {
          addResult(`üö´ Attempt ${i}: RATE LIMITED! ${message}`, false, status);
          addResult('üéâ Upload rate limiting working correctly!', true);
          break;
        } else if (status === 401) {
          addResult(`üîê Attempt ${i}: Need to login first`, false, status);
          break;
        } else {
          addResult(`‚ö†Ô∏è Attempt ${i}: ${message}`, false, status);
        }
      }

      await new Promise(resolve => setTimeout(resolve, 500));
    }

    setTesting(false);
  };

  const testGeneralRateLimit = async () => {
    setTesting(true);
    setResults([]);

    addResult('üöÄ B·∫Øt ƒë·∫ßu test General API Rate Limit (100 requests/15 ph√∫t)...');
    addResult('‚ö° Sending 10 rapid requests to test...');

    const promises = [];
    for (let i = 1; i <= 10; i++) {
      promises.push(
        api.get('/api/rbac/all-roles').catch(error => ({
          error: true,
          status: error.response?.status,
          message: error.response?.data?.message || error.message,
          attempt: i
        }))
      );
    }

    try {
      const results = await Promise.all(promises);

      let successCount = 0;
      let rateLimitCount = 0;

      results.forEach((result, index) => {
        if (result.error) {
          if (result.status === 429) {
            rateLimitCount++;
            addResult(`üö´ Request ${index + 1}: Rate limited`, false, 429);
          } else {
            addResult(`‚ùå Request ${index + 1}: ${result.message}`, false, result.status);
          }
        } else {
          successCount++;
          addResult(`‚úÖ Request ${index + 1}: Success`, true, 200);
        }
      });

      addResult(`üìä Summary: ${successCount} success, ${rateLimitCount} rate limited`);

    } catch (error) {
      addResult(`‚ùå Test failed: ${error.message}`, false);
    }

    setTesting(false);
  };

  const runTest = () => {
    switch (testType) {
      case 'login':
        testLoginRateLimit();
        break;
      case 'upload':
        testUploadRateLimit();
        break;
      case 'general':
        testGeneralRateLimit();
        break;
      default:
        break;
    }
  };

  return (
    <div className="rate-limit-demo">
      <h3>üõ°Ô∏è Rate Limit Testing</h3>

      <div className="test-controls">
        <select 
          value={testType} 
          onChange={(e) => setTestType(e.target.value)}
          disabled={testing}
        >
          <option value="login">Login Rate Limit (5/15min)</option>
          <option value="upload">Upload Rate Limit (3/min)</option>
          <option value="general">General API Limit (100/15min)</option>
        </select>

        <button 
          onClick={runTest} 
          disabled={testing}
          className="test-btn"
        >
          {testing ? 'Testing...' : 'Run Test'}
        </button>

        <button 
          onClick={clearResults}
          className="clear-btn"
        >
          Clear Results
        </button>
      </div>

      <div className="test-info">
        <h4>‚ÑπÔ∏è Rate Limits:</h4>
        <ul>
          <li><strong>Login:</strong> 5 failed attempts per 15 minutes</li>
          <li><strong>Upload:</strong> 3 uploads per minute</li>
          <li><strong>Password Reset:</strong> 3 attempts per hour</li>
          <li><strong>General API:</strong> 100 requests per 15 minutes</li>
        </ul>
      </div>

      <div className="test-results">
        {results.map(result => (
          <div 
            key={result.id} 
            className={`test-result ${result.success ? 'success' : 'error'}`}
          >
            <span className="timestamp">[{result.timestamp}]</span>
            <span className="status-code">[{result.statusCode}]</span>
            <span className="message">{result.message}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RateLimitDemo;
