import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserCard from './UserCard';

const AdminPanel = ({ onBack, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUsers();
    fetchStats();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data.users);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫£i danh s√°ch users'));
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/users/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);
    } catch (error) {
      console.error('Kh√¥ng th·ªÉ t·∫£i th·ªëng k√™:', error);
    }
  };

  const handleDeleteUser = async (userId, userName) => {
    if (!window.confirm(`B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a user "${userName}"?`)) return;

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.delete(`http://localhost:3000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage('‚úÖ ' + response.data.message);
      fetchUsers();
      fetchStats();
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'X√≥a user th·∫•t b·∫°i'));
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className="admin-container">
      <div className="admin-header">
        <button onClick={onBack} className="btn btn-secondary">
          ‚Üê Trang ch·ªß
        </button>
        <div className="admin-title">
          <h1>üîê Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h1>
          <p>Qu·∫£n l√Ω v√† gi√°m s√°t t·∫•t c·∫£ ng∆∞·ªùi d√πng trong h·ªá th·ªëng</p>
        </div>
        <button onClick={onLogout} className="btn btn-danger">
          üöÄ ƒêƒÉng xu·∫•t
        </button>
      </div>

      {stats && (
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-number">{stats.totalUsers}</div>
            <div className="stat-label">üë• T·ªïng users</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{stats.adminCount}</div>
            <div className="stat-label">üîë Admins</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{stats.userCount}</div>
            <div className="stat-label">üë§ Users</div>
          </div>
        </div>
      )}

      <div className="users-section">
        <div className="section-header">
          <div className="section-title">
            <h2>üìã Danh s√°ch ng∆∞·ªùi d√πng</h2>
            <span className="user-count">{users.length} ng∆∞·ªùi d√πng</span>
          </div>
          <div className="section-actions">
            <button onClick={fetchUsers} className="btn btn-refresh" disabled={loading}>
              {loading ? '‚è≥ ƒêang t·∫£i...' : 'üîÑ L√†m m·ªõi'}
            </button>
          </div>
        </div>

        {loading && users.length === 0 ? (
          <div className="loading">‚è≥ ƒêang t·∫£i danh s√°ch...</div>
        ) : (
          <div className="users-list">
            {users.map(user => (
              <UserCard 
                key={user._id}
                user={user}
                onDelete={handleDeleteUser}
                loading={loading}
              />
            ))}
          </div>
        )}

        {users.length === 0 && !loading && (
          <div className="empty-state">
            <div className="empty-icon">üì≠</div>
            <h3>Ch∆∞a c√≥ ng∆∞·ªùi d√πng</h3>
            <p>H·ªá th·ªëng ch∆∞a c√≥ ng∆∞·ªùi d√πng n√†o ƒëƒÉng k√Ω</p>
          </div>
        )}
      </div>

      {message && (
        <div className={`message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
          {message}
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
