import React, { useState } from 'react';
import axios from 'axios';

const UploadAvatar = ({ onBack, onSuccess }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [avatarUrl, setAvatarUrl] = useState('');
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [uploadMethod, setUploadMethod] = useState('file'); // 'file' or 'url'

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      setMessage('‚ùå Vui l√≤ng ch·ªçn file ·∫£nh');
      return;
    }

    const formData = new FormData();
    formData.append('avatar', selectedFile);

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/api/upload-avatar', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage('‚úÖ ' + response.data.message);
      setTimeout(() => onSuccess(), 2000);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'Upload th·∫•t b·∫°i'));
    } finally {
      setLoading(false);
    }
  };

  const handleUrlUpload = async () => {
    if (!avatarUrl) {
      setMessage('‚ùå Vui l√≤ng nh·∫≠p URL ·∫£nh');
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/avatar-url', {
        avatarUrl
      }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      setMessage('‚úÖ ' + response.data.message);
      setTimeout(() => onSuccess(), 2000);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <button onClick={onBack} className="btn btn-secondary back-btn">
          ‚Üê Quay l·∫°i
        </button>
        <h1>üñºÔ∏è Upload Avatar</h1>
        <p>Ch·ªçn ·∫£nh ƒë·∫°i di·ªán m·ªõi</p>
      </div>

      <div className="form-container">
        <div className="upload-tabs">
          <button 
            onClick={() => setUploadMethod('file')}
            className={`tab ${uploadMethod === 'file' ? 'active' : ''}`}
          >
            üìÅ Upload File
          </button>
          <button 
            onClick={() => setUploadMethod('url')}
            className={`tab ${uploadMethod === 'url' ? 'active' : ''}`}
          >
            üîó T·ª´ URL
          </button>
        </div>

        {uploadMethod === 'file' ? (
          <div className="upload-section">
            <div className="file-input-wrapper">
              <input
                type="file"
                accept="image/*"
                onChange={handleFileSelect}
                className="file-input"
                id="avatar-file"
              />
              <label htmlFor="avatar-file" className="file-label">
                üì∑ Ch·ªçn ·∫£nh t·ª´ m√°y t√≠nh
              </label>
            </div>

            {preview && (
              <div className="preview-section">
                <h3>üëÄ Preview:</h3>
                <div className="avatar-preview">
                  <img src={preview} alt="Preview" />
                </div>
              </div>
            )}

            <button 
              onClick={handleFileUpload}
              disabled={loading || !selectedFile}
              className="btn btn-primary"
            >
              {loading ? '‚è≥ ƒêang upload...' : 'üì§ Upload Avatar'}
            </button>
          </div>
        ) : (
          <div className="url-section">
            <div className="form-group">
              <label>üîó URL ·∫£nh:</label>
              <input
                type="url"
                value={avatarUrl}
                onChange={(e) => setAvatarUrl(e.target.value)}
                className="form-input"
                placeholder="https://example.com/avatar.jpg"
              />
            </div>

            {avatarUrl && (
              <div className="preview-section">
                <h3>üëÄ Preview:</h3>
                <div className="avatar-preview">
                  <img src={avatarUrl} alt="Preview" onError={(e) => e.target.style.display = 'none'} />
                </div>
              </div>
            )}

            <button 
              onClick={handleUrlUpload}
              disabled={loading || !avatarUrl}
              className="btn btn-primary"
            >
              {loading ? '‚è≥ ƒêang c·∫≠p nh·∫≠t...' : 'üîÑ C·∫≠p nh·∫≠t Avatar'}
            </button>
          </div>
        )}

        {message && (
          <div className={`message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
};

export default UploadAvatar;
