import React, { useState } from 'react';
import api from '../services/api';

const RefreshTokenTest = () => {
  const [testResults, setTestResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const addResult = (message, success = true) => {
    setTestResults(prev => [...prev, {
      id: Date.now(),
      message,
      success,
      timestamp: new Date().toLocaleTimeString()
    }]);
  };

  const testAutoRefresh = async () => {
    setLoading(true);
    setTestResults([]);

    try {
      addResult('üöÄ B·∫Øt ƒë·∫ßu test auto refresh token...');

      // Test 1: Call protected API (should work with current token)
      addResult('üì° Test 1: G·ªçi API v·ªõi token hi·ªán t·∫°i...');
      const response1 = await api.get('/api/profile');
      addResult(`‚úÖ API call th√†nh c√¥ng: ${response1.data.user.name}`);

      // Test 2: Wait for token to expire (simulate)
      addResult('‚è≥ Test 2: ƒê·ª£i token h·∫øt h·∫°n (15 ph√∫t)...');
      addResult('üí° Trong th·ª±c t·∫ø, token s·∫Ω t·ª± ƒë·ªông refresh khi h·∫øt h·∫°n');

      // Test 3: Force another API call
      addResult('üì° Test 3: G·ªçi API l·∫ßn n·ªØa...');
      const response2 = await api.get('/api/profile');
      addResult(`‚úÖ API call th√†nh c√¥ng (c√≥ th·ªÉ ƒë√£ refresh): ${response2.data.user.name}`);

      addResult('üéâ Test ho√†n th√†nh! Auto refresh ho·∫°t ƒë·ªông t·ªët.');

    } catch (error) {
      addResult(`‚ùå Test th·∫•t b·∫°i: ${error.message}`, false);
    } finally {
      setLoading(false);
    }
  };

  const clearResults = () => {
    setTestResults([]);
  };

  return (
    <div className="refresh-test">
      <h3>üß™ Auto Refresh Token Test</h3>

      <div className="test-controls">
        <button 
          onClick={testAutoRefresh} 
          disabled={loading}
          className="test-btn"
        >
          {loading ? 'ƒêang test...' : 'Ch·∫°y Test Auto Refresh'}
        </button>

        <button 
          onClick={clearResults}
          className="clear-btn"
        >
          X√≥a k·∫øt qu·∫£
        </button>
      </div>

      <div className="test-results">
        {testResults.map(result => (
          <div 
            key={result.id} 
            className={`test-result ${result.success ? 'success' : 'error'}`}
          >
            <span className="timestamp">[{result.timestamp}]</span>
            <span className="message">{result.message}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RefreshTokenTest;
