import React, { useState, useEffect } from 'react';
import api from '../services/api';

const RoleManagement = () => {
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [usersResponse, statsResponse] = await Promise.all([
        api.get('/api/users'),
        api.get('/api/rbac/stats')
      ]);

      setUsers(usersResponse.data.users);
      setStats(statsResponse.data.stats);
    } catch (error) {
      setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu');
    } finally {
      setLoading(false);
    }
  };

  const updateUserRole = async (userId, newRole) => {
    try {
      setError('');
      setSuccess('');

      await api.put(`/api/rbac/users/${userId}/role`, { role: newRole });

      setSuccess(`ƒê√£ c·∫≠p nh·∫≠t role th√†nh ${newRole}`);
      fetchData(); // Refresh data
    } catch (error) {
      setError(error.response?.data?.message || 'Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t role');
    }
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'admin': return 'role-badge admin';
      case 'moderator': return 'role-badge moderator';
      default: return 'role-badge user';
    }
  };

  if (loading) return <div className="loading">ƒêang t·∫£i...</div>;

  return (
    <div className="role-management">
      <h2>üîê Role Management (Admin Only)</h2>

      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}

      {/* Role Statistics */}
      {stats && (
        <div className="role-stats">
          <h3>üìä Th·ªëng k√™ Role</h3>
          <div className="stats-grid">
            <div className="stat-card admin">
              <h4>Admin</h4>
              <div className="stat-number">{stats.admin}</div>
            </div>
            <div className="stat-card moderator">
              <h4>Moderator</h4>
              <div className="stat-number">{stats.moderator}</div>
            </div>
            <div className="stat-card user">
              <h4>User</h4>
              <div className="stat-number">{stats.user}</div>
            </div>
          </div>
        </div>
      )}

      {/* Users Table */}
      <div className="users-role-table">
        <h3>üë• Qu·∫£n l√Ω Role Users</h3>
        <table>
          <thead>
            <tr>
              <th>T√™n</th>
              <th>Email</th>
              <th>Role hi·ªán t·∫°i</th>
              <th>Thay ƒë·ªïi Role</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <span className={getRoleBadgeClass(user.role)}>
                    {user.role}
                  </span>
                </td>
                <td>
                  <div className="role-actions">
                    {user.role !== 'user' && (
                      <button 
                        onClick={() => updateUserRole(user._id, 'user')}
                        className="role-btn user"
                      >
                        ‚Üí User
                      </button>
                    )}
                    {user.role !== 'moderator' && (
                      <button 
                        onClick={() => updateUserRole(user._id, 'moderator')}
                        className="role-btn moderator"
                      >
                        ‚Üí Moderator
                      </button>
                    )}
                    {user.role !== 'admin' && (
                      <button 
                        onClick={() => updateUserRole(user._id, 'admin')}
                        className="role-btn admin"
                      >
                        ‚Üí Admin
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default RoleManagement;
