import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { logoutUser } from '../store/authSlice';
import AvatarUpload from './AvatarUpload';
import RefreshTokenTest from './RefreshTokenTest';
import RateLimitDemo from './RateLimitDemo';

const SettingsRedux = () => {
  const { user } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('profile');
  const [currentAvatar, setCurrentAvatar] = useState(user?.avatar || '');

  const handleLogoutAll = () => {
    if (window.confirm('ƒêƒÉng xu·∫•t kh·ªèi t·∫•t c·∫£ thi·∫øt b·ªã?')) {
      dispatch(logoutUser());
      navigate('/login');
    }
  };

  const tabs = [
    { id: 'profile', label: 'üë§ Profile', icon: 'üë§' },
    { id: 'avatar', label: 'üñºÔ∏è Avatar', icon: 'üñºÔ∏è' },
    { id: 'security', label: 'üîí Security', icon: 'üîí' },
    { id: 'tokens', label: 'üîë Tokens', icon: 'üîë' },
    { id: 'testing', label: 'üß™ Testing', icon: 'üß™' }
  ];

  return (
    <div className="settings-container">
      <div className="settings-panel">
        <h2>‚öôÔ∏è Settings</h2>

        {/* Tabs */}
        <div className="settings-tabs">
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
            >
              {tab.icon} {tab.label}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {/* Profile Tab */}
          {activeTab === 'profile' && (
            <div className="profile-settings">
              <h3>üë§ Profile Information</h3>
              <div className="profile-card">
                <div className="profile-field">
                  <label>ID:</label>
                  <span>{user?.id}</span>
                </div>
                <div className="profile-field">
                  <label>T√™n:</label>
                  <span>{user?.name}</span>
                </div>
                <div className="profile-field">
                  <label>Email:</label>
                  <span>{user?.email}</span>
                </div>
                <div className="profile-field">
                  <label>Vai tr√≤:</label>
                  <span className={`role-badge ${user?.role}`}>{user?.role}</span>
                </div>
                <div className="profile-field">
                  <label>Permissions:</label>
                  <div className="permissions-list">
                    {user?.permissions?.map(permission => (
                      <span key={permission} className="permission-badge">
                        {permission}
                      </span>
                    ))}
                  </div>
                </div>
              </div>

              <button onClick={() => navigate('/profile')} className="edit-btn">
                ‚úèÔ∏è Edit Profile
              </button>
            </div>
          )}

          {/* Avatar Tab */}
          {activeTab === 'avatar' && (
            <div className="avatar-settings">
              <h3>üñºÔ∏è Avatar Settings</h3>
              <AvatarUpload 
                currentAvatar={currentAvatar}
                onAvatarUpdate={(newAvatar) => setCurrentAvatar(newAvatar)}
              />
            </div>
          )}

          {/* Security Tab */}
          {activeTab === 'security' && (
            <div className="security-settings">
              <h3>üîí Security Settings</h3>

              <div className="security-section">
                <h4>üîë Password</h4>
                <p>ƒê·ªïi m·∫≠t kh·∫©u ƒë·ªÉ b·∫£o m·∫≠t t√†i kho·∫£n</p>
                <button onClick={() => navigate('/forgot-password')} className="security-btn">
                  üîÑ Change Password
                </button>
              </div>

              <div className="security-section">
                <h4>üö™ Sessions</h4>
                <p>ƒêƒÉng xu·∫•t kh·ªèi t·∫•t c·∫£ thi·∫øt b·ªã</p>
                <button onClick={handleLogoutAll} className="danger-btn">
                  üö™ Logout All Devices
                </button>
              </div>

              <div className="security-section">
                <h4>üìä Account Info</h4>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Role:</span>
                    <span className={`role-badge ${user?.role}`}>{user?.role}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Permissions:</span>
                    <span>{user?.permissions?.length || 0} permissions</span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Tokens Tab */}
          {activeTab === 'tokens' && (
            <div className="tokens-settings">
              <h3>üîë Token Management</h3>
              <RefreshTokenTest />
            </div>
          )}

          {/* Testing Tab */}
          {activeTab === 'testing' && (
            <div className="testing-settings">
              <h3>üß™ Testing Tools</h3>
              <RateLimitDemo />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SettingsRedux;
