import React, { useState } from 'react';
import axios from 'axios';

const ForgotPassword = ({ onBack, onResetToken }) => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [resetToken, setResetToken] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:3000/api/auth/forgot-password', {
        email
      });

      setResetToken(response.data.resetToken);
      setMessage('‚úÖ ' + response.data.message);
      onResetToken(response.data.resetToken);
    } catch (error) {
      setMessage('‚ùå ' + (error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <button onClick={onBack} className="btn btn-secondary back-btn">
          ‚Üê Quay l·∫°i
        </button>
        <h1>üîë Qu√™n m·∫≠t kh·∫©u</h1>
        <p>Nh·∫≠p email ƒë·ªÉ nh·∫≠n token reset m·∫≠t kh·∫©u</p>
      </div>

      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>üìß Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="form-input"
              placeholder="Nh·∫≠p email c·ªßa b·∫°n"
            />
          </div>

          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? '‚è≥ ƒêang g·ª≠i...' : 'üì§ G·ª≠i token reset'}
          </button>
        </form>

        {message && (
          <div className={`message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}

        {resetToken && (
          <div className="token-display">
            <h3>üé´ Reset Token</h3>
            <div className="token-box">
              <code>{resetToken}</code>
              <button 
                onClick={() => navigator.clipboard.writeText(resetToken)}
                className="btn btn-secondary btn-small"
              >
                üìã Copy
              </button>
            </div>
            <p className="token-note">
              ‚è∞ Token c√≥ hi·ªáu l·ª±c trong 10 ph√∫t. S·ª≠ d·ª•ng token n√†y ƒë·ªÉ ƒë·ªïi m·∫≠t kh·∫©u.
            </p>
