import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';

const UsersRedux = () => {
  const { user, token } = useSelector((state) => state.auth);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [formData, setFormData] = useState({ name: '', email: '' });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    try {
      console.log('Fetching users with token:', token ? 'Token exists' : 'No token');
      const response = await axios.get('http://localhost:3000/api/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Users response:', response.data);
      setUsers(response.data.users || []);
    } catch (error) {
      console.error('Error fetching users:', error.response?.data || error.message);
      const errorMsg = error.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫£i danh s√°ch users';
      setError(`L·ªói: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingUser) {
        await axios.put(`http://localhost:3000/api/users/${editingUser._id}`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
      } else {
        await axios.post('http://localhost:3000/api/users', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
      setFormData({ name: '', email: '' });
      setShowAddForm(false);
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      setError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra');
    }
  };

  const handleEdit = (userToEdit) => {
    setEditingUser(userToEdit);
    setFormData({ name: userToEdit.name, email: userToEdit.email });
    setShowAddForm(true);
  };

  const handleDelete = async (userId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a user n√†y?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchUsers();
    } catch (error) {
      setError('Kh√¥ng th·ªÉ x√≥a user');
    }
  };

  const canEdit = user?.role === 'admin' || user?.role === 'moderator';
  const canDelete = user?.role === 'admin';
  const canAdd = user?.role === 'admin';

  if (loading) return <div className="loading">ƒêang t·∫£i...</div>;

  return (
    <div className="users-container">
      <div className="users-panel">
        <div className="users-header">
          <h2>üë• Qu·∫£n l√Ω Users</h2>
          {canAdd && (
            <button 
              onClick={() => setShowAddForm(!showAddForm)}
              className="add-btn"
            >
              {showAddForm ? '‚ùå H·ªßy' : '‚ûï Th√™m User'}
            </button>
          )}
        </div>

        {error && <div className="error">{error}</div>}

        {/* Add/Edit Form */}
        {showAddForm && (
          <div className="add-user-form">
            <h3>{editingUser ? '‚úèÔ∏è Ch·ªânh s·ª≠a User' : '‚ûï Th√™m User M·ªõi'}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <input
                  type="text"
                  placeholder="T√™n"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                />
                <input
                  type="email"
                  placeholder="Email"
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                  required
                />
                <button type="submit" className="submit-btn">
                  {editingUser ? 'üíæ C·∫≠p nh·∫≠t' : '‚ûï Th√™m'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Users Table */}
        <div className="users-table">
          <div className="table-header">
            <h3>üìã Danh s√°ch Users ({users.length})</h3>
          </div>

          <table>
            <thead>
              <tr>
                <th>Avatar</th>
                <th>T√™n</th>
                <th>Email</th>
                <th>Vai tr√≤</th>
                <th>Ng√†y t·∫°o</th>
                {(canEdit || canDelete) && <th>H√†nh ƒë·ªông</th>}
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u._id}>
                  <td>
                    <div className="avatar-display avatar-small">
                      {u.avatar ? (
                        <img src={u.avatar} alt={u.name} className="avatar-img" />
                      ) : (
                        <div className="avatar-fallback">
                          {u.name?.charAt(0)?.toUpperCase()}
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="user-name">{u.name}</td>
                  <td className="user-email">{u.email}</td>
                  <td>
                    <span className={`role-badge ${u.role}`}>{u.role}</span>
                  </td>
                  <td className="created-date">
                    {new Date(u.createdAt).toLocaleDateString()}
                  </td>
                  {(canEdit || canDelete) && (
                    <td className="actions">
                      {canEdit && (
                        <button 
                          onClick={() => handleEdit(u)}
                          className="edit-btn"
                        >
                          ‚úèÔ∏è S·ª≠a
                        </button>
                      )}
                      {canDelete && u._id !== user.id && (
                        <button 
                          onClick={() => handleDelete(u._id)}
                          className="delete-btn"
                        >
                          üóëÔ∏è X√≥a
                        </button>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UsersRedux;
