import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';

const AdminRedux = () => {
  const { user, token } = useSelector((state) => state.auth);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3000/api/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data.users);
    } catch (error) {
      setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch users');
    } finally {
      setLoading(false);
    }
  };

  const deleteUser = async (userId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a user n√†y?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchUsers();
    } catch (error) {
      setError('Kh√¥ng th·ªÉ x√≥a user');
    }
  };

  if (loading) return <div>ƒêang t·∫£i...</div>;

  return (
    <div className="admin-container">
      <div className="admin-panel">
        <h2>Admin Panel</h2>

        <div className="stats-grid">
          <div className="stat-card">
            <h3>T·ªïng Users</h3>
            <div className="stat-number">{users.length}</div>
          </div>
          <div className="stat-card">
            <h3>Admin: {user?.name}</h3>
            <div className="stat-number">üëë</div>
          </div>
        </div>

        {error && <div className="error">{error}</div>}

        <div className="users-table">
          <h3>Danh s√°ch Users ({users.length})</h3>
          <table>
            <thead>
              <tr>
                <th>T√™n</th>
                <th>Email</th>
                <th>Vai tr√≤</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u._id}>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td><span className={`role-badge ${u.role}`}>{u.role}</span></td>
                  <td>
                    {u._id !== user.id && (
                      <button 
                        onClick={() => deleteUser(u._id)}
                        className="delete-btn"
                      >
                        X√≥a
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminRedux;
