import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const ResetPassword = () => {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);
  const [countdown, setCountdown] = useState(5);
  const { token } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      setError('Token kh√¥ng h·ª£p l·ªá');
    }
  }, [token]);

  useEffect(() => {
    if (isSuccess && countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    } else if (isSuccess && countdown === 0) {
      navigate('/');
    }
  }, [isSuccess, countdown, navigate]);

  const checkPasswordStrength = (password) => {
    if (password.length < 6) return 'Y·∫øu';
    if (password.length < 8) return 'Trung b√¨nh';
    if (password.match(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/)) {
      return 'M·∫°nh';
    }
    return 'Kh√°';
  };

  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    setPasswordStrength(checkPasswordStrength(newPassword));
  };

  const validateForm = () => {
    if (!password.trim()) {
      setError('Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u m·ªõi');
      return false;
    }

    if (password.length < 6) {
      setError('M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±');
      return false;
    }

    if (!confirmPassword.trim()) {
      setError('Vui l√≤ng x√°c nh·∫≠n m·∫≠t kh·∫©u');
      return false;
    }

    if (password !== confirmPassword) {
      setError('M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    setError('');
    setMessage('');

    try {
      const response = await axios.post(`http://localhost:3000/api/auth/reset-password/${token}`, {
        password: password.trim()
      });
      setMessage(response.data.message);
      setIsSuccess(true);
    } catch (error) {
      setError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra khi ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u');
    } finally {
      setLoading(false);
    }
  };

  if (isSuccess) {
    return (
      <div className="auth-container">
        <div className="auth-form success-form">
          <div className="success-icon">‚úì</div>
          <h2>M·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t l·∫°i!</h2>
          <p className="success-message">{message}</p>
          <p className="redirect-message">
            B·∫°n s·∫Ω ƒë∆∞·ª£c chuy·ªÉn h∆∞·ªõng ƒë·∫øn trang ƒëƒÉng nh·∫≠p trong {countdown} gi√¢y...
          </p>
          <button 
            type="button" 
            className="login-now-btn"
            onClick={() => navigate('/')}
          >
            ƒêƒÉng nh·∫≠p ngay
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u</h2>

        <p className="reset-password-description">
          Nh·∫≠p m·∫≠t kh·∫©u m·ªõi cho t√†i kho·∫£n c·ªßa b·∫°n.
        </p>

        {error && <div className="error">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <div className="password-input-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="M·∫≠t kh·∫©u m·ªõi"
                value={password}
                onChange={handlePasswordChange}
                disabled={loading}
                autoFocus
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>
            {password && (
              <div className={`password-strength ${passwordStrength.toLowerCase()}`}>
                ƒê·ªô m·∫°nh: {passwordStrength}
              </div>
            )}
          </div>

          <div className="form-group">
            <div className="password-input-wrapper">
              <input
                type={showConfirmPassword ? 'text' : 'password'}
                placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u m·ªõi"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                disabled={loading}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              >
                {showConfirmPassword ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>
            {confirmPassword && password !== confirmPassword && (
              <div className="password-mismatch">
                M·∫≠t kh·∫©u kh√¥ng kh·ªõp
              </div>
            )}
          </div>

          <div className="password-requirements">
            <p>M·∫≠t kh·∫©u ph·∫£i c√≥:</p>
            <ul>
              <li className={password.length >= 6 ? 'valid' : ''}>√çt nh·∫•t 6 k√Ω t·ª±</li>
              <li className={password.match(/[A-Z]/) ? 'valid' : ''}>M·ªôt ch·ªØ c√°i vi·∫øt hoa</li>
              <li className={password.match(/[0-9]/) ? 'valid' : ''}>M·ªôt ch·ªØ s·ªë</li>
            </ul>
          </div>

          <button 
            type="submit" 
            disabled={loading || !password || !confirmPassword || password !== confirmPassword}
            className="reset-password-btn"
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                ƒêang c·∫≠p nh·∫≠t...
              </>
            ) : (
              'ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u'
            )}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            <button 
              type="button" 
              className="link-btn"
              onClick={() => navigate('/')}
            >
              Quay l·∫°i trang ƒëƒÉng nh·∫≠p
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
