import React, { useState, useRef } from 'react';
import api from '../services/api';

const AvatarUpload = ({ currentAvatar, onAvatarUpdate }) => {
  const [uploading, setUploading] = useState(false);
  const [preview, setPreview] = useState(currentAvatar);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const fileInputRef = useRef(null);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        setError('Ch·ªâ cho ph√©p upload file ·∫£nh');
        return;
      }

      // Validate file size (5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('File kh√¥ng ƒë∆∞·ª£c v∆∞·ª£t qu√° 5MB');
        return;
      }

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreview(e.target.result);
      };
      reader.readAsDataURL(file);

      // Upload file
      uploadAvatar(file);
    }
  };

  const uploadAvatar = async (file) => {
    // Reset all states first
    setError('');
    setSuccess('');
    setUploading(true);

    try {
      const formData = new FormData();
      formData.append('avatar', file);

      const response = await api.post('/api/avatar/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSuccess('Upload avatar th√†nh c√¥ng!');
      // Use the avatar path from response, frontend will handle full URL
      setPreview(response.data.avatar);
      onAvatarUpdate(response.data.avatar);

    } catch (error) {
      setError(error.response?.data?.message || 'Upload th·∫•t b·∫°i');
      setPreview(currentAvatar); // Reset preview
    } finally {
      setUploading(false);
    }
  };

  const deleteAvatar = async () => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a avatar?')) return;

    // Reset all states first
    setError('');
    setSuccess('');
    setUploading(true);

    try {
      await api.delete('/api/avatar/delete');

      setSuccess('X√≥a avatar th√†nh c√¥ng!');
      setPreview(null);
      onAvatarUpdate(null);
    } catch (error) {
      setError(error.response?.data?.message || 'X√≥a avatar th·∫•t b·∫°i');
    } finally {
      setUploading(false);
    }
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="avatar-upload">
      <h4>üì∏ Avatar</h4>

      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}

      <div className="avatar-container">
        <div className="avatar-preview" onClick={triggerFileInput}>
          {preview ? (
            <img 
              src={preview.startsWith('http') ? preview : `http://localhost:3000${preview}`} 
              alt="Avatar" 
              className="avatar-image"
              onError={(e) => {
                console.error('Avatar load error:', e.target.src);
                setError('Kh√¥ng th·ªÉ t·∫£i ·∫£nh avatar');
              }}
            />
          ) : (
            <div className="avatar-placeholder">
              <span>üì∑</span>
              <p>Click ƒë·ªÉ upload</p>
            </div>
          )}
          {uploading && <div className="avatar-loading">Uploading...</div>}
        </div>

        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          style={{ display: 'none' }}
        />

        <div className="avatar-actions">
          <button 
            onClick={triggerFileInput}
            disabled={uploading}
            className="upload-btn"
          >
            {uploading ? 'ƒêang upload...' : 'Ch·ªçn ·∫£nh'}
          </button>

          {preview && (
            <button 
              onClick={deleteAvatar}
              disabled={uploading}
              className="delete-avatar-btn"
            >
              X√≥a avatar
            </button>
          )}
        </div>
      </div>

      <div className="upload-info">
        <p>‚Ä¢ Ch·ªâ cho ph√©p file ·∫£nh (JPG, PNG, GIF)</p>
        <p>‚Ä¢ K√≠ch th∆∞·ªõc t·ªëi ƒëa: 5MB</p>
        <p>‚Ä¢ ·∫¢nh s·∫Ω ƒë∆∞·ª£c resize th√†nh 300x300px</p>
      </div>
    </div>
  );
};

export default AvatarUpload;
